mapper.select:
select pl.ord, ry.name username, t.name companyName, pl.company, pl.intro, pl.tags, ry.id replyId, (select count(*) from tags tag2 where tag2.plan1=pl.ord and tag2.person=#{username}) selfTag, pl.date7, pl.isPeitong, pl.others others, dp.id dianping_id, dp.intro dianping_intro, dp.name dianping_name, dp.date7 dianping_date7, tag.id tag_id, tag.time tag_time, tag.person tag_person from plan1 pl left join tags tag on tag.plan1=pl.ord
left join gate g on pl.cateid=g.ord
left join tel t on t.ord = pl.company left join reply ry on ry.plan1 = pl.ord left join dianping dp on dp.ord=ry.id
where 1=1
and pl.cateid=#{ywyId}
and g.sorce=#{sorce}
and g.sorce in(2,3)
and pl.company=#{company}
and pl.date7>=#{createDate1}
and pl.date7<=#{createDate2}
and(t.name like '%${keyword}%' or ry.name like '%${keyword}%')
order by pl.date7 desc,dp.date7 asc

select
(select count(pl.ord) from plan1 pl left join gate g on g.ord = pl.order1 where 1=1
and g.sorce=#{sorce}

and g.sorce in(2,3)

and pl.order1=#{ywyId}
and
pl.date7>=#{year} ) year,
(select count(pl.ord) from plan1 pl left join gate g on g.ord = pl.order1 where 1=1
and g.sorce=#{sorce}

and g.sorce in(2,3)

and pl.order1=#{ywyId}
and
pl.date7>=#{month} ) month,
(select count(pl.ord) from plan1 pl left join gate g on g.ord = pl.order1 where 1=1
and g.sorce=#{sorce}

and g.sorce in(2,3)

and pl.order1=#{ywyId}
and
pl.date7>=#{week} ) week,
(select count(pl.ord) from plan1 pl left join gate g on g.ord = pl.order1 where 1=1
and g.sorce=#{sorce}

and g.sorce in(2,3)

and pl.order1=#{ywyId}
and
pl.date7>=#{day} ) day,
(select count(pl.ord) from plan1 pl left join gate g on g.ord = pl.order1 where 1=1
and g.sorce=#{sorce}

and g.sorce in(2,3)

and pl.order1=#{ywyId}
and
pl.date7>=#{quarter} ) quarter

select pl.cateid, ry.name ywy, pl.others, pl.date7, t.name companyName, pl.intro intro, g.sorce sorce, (select FValue from ERP_CustomValues where FieldsID=17 and OrderID=t.ord) yzjyl, (select FValue from ERP_CustomValues where FieldsID=16 and OrderID=t.ord) companyPersonNum, (select FValue from ERP_CustomValues where FieldsID=34 and OrderID=t.ord) companyGuimo, (select FValue from ERP_CustomValues where FieldsID=15 and OrderID=t.ord) isNew, (select FValue from ERP_CustomValues where FieldsID=27 and OrderID=t.ord) leixing, (select FValue from ERP_CustomValues where FieldsID=24 and OrderID=t.ord) wshz,
pl.address, p.sex linkSex, p.name linkName, p.msn linkHobby, p.mobile, t.product shangyou, t.c2 xiayou, t.c3 qita, p.job linkManJob from plan1 pl left join reply ry on ry.plan1 = pl.ord left join tel t on t.ord=pl.company left join person p on p.ord=pl.person left join gate g on g.ord = pl.cateid where pl.cateid = #{id} and pl.company in ( select company from plan1 pl2 where pl2.cateid = #{id}
and pl2.date7 >= #{startDate}
and pl2.date7 <= #{endDate}
)
and pl.date7 >= #{closeThree}
order by pl.date7 desc
